INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('1','বাংলাদেশ পোলিওমুক্ত','','বিশ্ব স্বাস্থ্য সংস্থা (ডব্লিউএইচও) বাংলাদেশসহ দক্ষিণ-পূর্ব এশিয়ার ১১টি দেশকে পোলিওমুক্ত ঘোষণা করেছে। আজ বৃহস্পতিবার ভারতের নয়াদিল্লিতে বিশ্ব স্বাস্থ্য সংস্থার আঞ্চলিক কার্যালয় থেকে বাংলাদেশকে এই পোলিওমুক্ত সনদ নেয়া হয়। এ উপলক্ষে ডব্লিউএইচওর আঞ্চলিক সনদ কমিটির সদস্য অধ্যাপক মাহমুদুর রহমান জানান, জনস্বাস্থ্যের ক্ষেত্রে এই অর্জন এ অঞ্চলের জন্য একটি মাইলফলক। বাংলাদেশের স্বাস্থ্য প্রতিমন্ত্রী জাহিদ মালেকসহ ১১টি দেশের প্রতিনিধিরা এ অনুষ্ঠানে উপস্থিত ছিলেন।
এদিকে এ অঞ্চলের পোলিওমুক্ত বাকি ১০টি দেশ হলো ভুটান, উত্তর কোরিয়া, ভারত, ইন্দোনেশিয়া, মালদ্বীপ, মিয়ানমার, নেপাল, শ্রীলঙ্কা, থাইল্যান্ড ও পূর্ব তিমুর। গত ৩ বছরে এ দেশগুলোতে একজনও পোলিও রোগী পাওয়া যায়নি। এ প্রেক্ষাপটে এ অঞ্চলকে পোলিওমুক্ত সনদ দিয়েছে ডব্লিউএইচও। এর মাধ্যমে সংস্তার ৬টি অঞ্চলের মধ্যে ৪টিই পোলিওমুক্ত সনদ পেল।
অন্যদিকে ডব্লিউএইচওর আফ্রিকা ও পূর্ব ভূমধ্যসাগরীয় অঞ্চলের সবগুলো দেশ যদি টানা তিন বছর পোলিওমুক্ত থাকতে পারে তাহলে পুরো বিশ্বকেই পোলিওমুক্ত ঘোষণা করা সম্ভব হবে। পূর্ব ভূমধ্যসাগরীয় অঞ্চলের দেশ পাকিস্তান ও আফগানিস্তানে এখনো পোলিওর প্রকোপ রয়েছে। পাকিস্তানের সাথে ভারতের দীর্ঘ সীমান্ত থাকায় দক্ষিণ পূর্ব এশিয়ার জন্যও এ দুটি দেশ ঝুঁকি তৈরি করছে।','অনলাইন প্রতিবেদক','0','2018-02-16 16:15:08.605590','2018-02-16 16:15:08.605640');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('2','ইউক্রেন কি চলেই যাবে রাশিয়ার দখলে?','','কদিন আগেও ক্রিমিয়া নামটি সবার কাছে খুব বেশি পরিচিত ছিল না। ইউক্রেনের প্রত্যন্ত এ অঞ্চলটি ইতিহাসবিদদের কাছে কিছুটা পরিচিত ১৮ শতকের পঞ্চাশের দশকের একটি যুদ্ধের কারণে। কিন্তু হঠাৎ করেই এবং অপ্রত্যাশিতভাবে ক্রিমিয়া রাশিয়া ও পশ্চিমাদের মধ্যে দ্বন্দ্বের কেন্দ্রবিন্দু হয়ে উঠেছে। মস্কো নাটকীয়ভাবে অঞ্চলটি দখল করে নেওয়ার পর সবার নজর এখন ক্রিমিয়ার দিকে। উপদ্বীপটি কার্যত এখন রুশ সেনাদের নিয়ন্ত্রণে, অথচ এলাকাটির ওপর সার্বভৌম অধিকার ইউক্রেনের। ক্রিমিয়ায় রয়েছে রাশিয়ার নৌবাহিনীর সাব ইউনিট ব্ল্যাক সি ফ্লিটের একটি গুরুত্বপূর্ণ ঘাঁটি এবং রুশ ভাষাভাষীর বিপুলসংখ্যক মানুষ। ২০১০ সালে এক চুক্তির আওতায় ওই নৌঘাঁটির ইজারার মেয়াদ ২০৪২ সাল পর্যন্ত বাড়ায় ইউক্রেন। ক্রিমিয়ায় রুশ সেনার অনুপ্রবেশে অনেকে মনে করছেন, এটি ইউক্রেন দখলের প্রথম ধাপ। রাশিয়া ধীরে ধীরে ইউক্রেনের দক্ষিণ ও পূর্বাঞ্চলের অন্যান্য অংশও দখল করে নেবে। তবে সব লক্ষণ বিচারে এটা স্পষ্ট যে খুব শিগগিরই এই দখলদারি ছাড়বে না রাশিয়া। অনেকে বলছে, এটা প্রেসিডেন্ট পুতিনের সুস্পষ্ট ‘সাম্রাজ্যবাদী আগ্রাসন’। ক্রেমলিনের বর্তমান নেতৃত্ব কখনোই স্বাধীন ও সার্বভৌম ইউক্রেনকে মেনে নেয়নি। সাবেক সোভিয়েত ইউনিয়ন ভেঙে খান খান হয়ে যাওয়ায় ১৯৯১ সালে দেশটি স্বাধীনতা লাভ করে। ব্রাসেলসের গবেষণা প্রতিষ্ঠান কার্নেগি ইউরোপের কর্মকর্তা আলরিচ স্পেক বলেন, ইউক্রেনের সমস্যাকে ক্রেমলিন সব সময় তাদের নিজস্ব ব্যাপার বলে মনে করে। কারণ, ওই ভূখণ্ড তাদের পুরোনো সাম্রাজেরই অংশ। এখন অবস্থাদৃষ্টে মনে হচ্ছে, ১৮ শতকের শেষে রাশিয়ার দখলে আসা ক্রিমিয়াকে ৬০ বছর আগে সাবেক সোভিয়েত ইউনিয়নের প্রয়াত প্রেসিডেন্ট নিকিতা খ্রুশ্চভ ইউক্রেনের কাছে ছেড়ে দিয়ে যে ভুল করেছিলেন, পুতিন তা সংশোধন করতে চান। বিভিন্ন সময়ে পুতিনের কথাবার্তায়ও তা বোঝা গেছে। ২০০৮ সালে রোমানিয়ার বুখারেস্টে ভ্লাদিমির পুতিন তৎকালীন মার্কিন প্রেসিডেন্ট জর্জ ডব্লিউ বুশকে এ কথা বলে চমকে দিয়েছিলেন, আপনাকে এটা বুঝতে হবে যে ইউক্রেন কোনো দেশ নয়। এটা পূর্ব ইউরোপের একটি ভূখণ্ড এবং এর বৃহত্তর অংশ আমাদের দেওয়া হয়েছে। ছয় বছর পর ক্রেমলিন যেন এটা নিশ্চিত করতে চাইছে যে পুতিনের সেদিনের কথাগুলো এখন বাস্তবে রূপ নিচ্ছে। পশ্চিমাদের বক্তব্যের ব্যাপারে মস্কো বলছে অন্য কথা। ক্রিমিয়ায় রুশ জনগোষ্ঠীকে রক্ষা করাই তাদের উদ্দেশ্য। ইউক্রেনে রুশ জনগোষ্ঠীর সঙ্গে তাতার ও অন্যান্য গোষ্ঠীর বিরোধ আছে। রাজনৈতিক তথা কূটনৈতিক বিবাদের পরিপ্রেক্ষিতে তা জটিল রূপ ধারণ করেছে। একদিকে রাশিয়ার সঙ্গে থাকার পুরোনো রীতি অনুসরণ, অন্যদিকে ইউরোপীয় ইউনিয়নের সঙ্গে সম্পর্ক উন্নত করে নতুন যুগে উত্তরণ—এই নিয়ে বিবাদের সূত্রপাত। গত বছরের নভেম্বর মাস থেকে এই নিয়েই ছিল ইউক্রেন উত্তাল। নাগরিকদের একটি অংশ মনে করছে মস্কোর সঙ্গে থাকলেই তাদের ভালো হবে। অন্য একটি অংশ মনে করছে মস্কোর প্রভাববলয় ছেড়ে ইউরোপের অংশীদার হলেই তাদের উন্নতি হবে। আর এই বিবাদ থেকে সূত্রপাত হয় আন্দোলনের। ২০০০ সালে রাশিয়ার ক্ষমতায় বসার পর থেকেই পুতিনের মূল লক্ষ্য হচ্ছে ন্যাটো বা ইউরোপীয় ইউনিয়নের প্রভাব থেকে সাবেক সোভিয়েতভুক্ত দেশগুলোকে মুক্ত রাখা। আর এ কারণে ইউরোপীয় ইউনয়নের সঙ্গে বাণিজ্য চুক্তি না করতে পুতিন ইউক্রেনের বিতাড়িত প্রেসিডেন্ট ইয়ানুকোভিচকে দেড় হাজার কোটি ডলার ঋণ দেওয়ার প্রস্তাব করেছিলেন। যা-ই হোক আন্দোলনের মুখে রুশপন্থী প্রেসিডেন্ট ভিক্তর ইয়ানুকোভিচ গত মাসে ক্ষমতা ছেড়ে রাশিয়ায় পালিয়ে যান। ইয়ানুকোভিচের পতনের পরপরই ক্রিমিয়া দখলে নেয় রুশ সেনারা। যুক্তরাষ্ট্রের জর্জটাউন ইউনিভার্সিটির স্কুল অব ফরেন সার্ভিসের অধ্যাপক অ্যাঞ্জেলা স্টেন্ট বলেন, ইয়ানুকোভিচের পতনে ভয় পেয়েছে রাশিয়া। তারা ভেবেছে, এবার হয়তো নৌঘাঁটির ইজারা তারা হারাবে এবং এখান থেকে তাদের নৌঘাঁটি সরিয়ে নিতে হতে পারে। আর তা থেকেই এই ক্রিমিয়া-দখল। এদিকে ইউক্রেনের সামরিক বাহিনী জানিয়েছে, তারা মস্কোর বিরুদ্ধে কোনো ধরনের লড়াইয়ে যাবে না। কেননা ইউক্রেনের সামরিক বাহিনীতে তহবিল ও অস্ত্রশস্ত্রের সংকট রয়েছে। তাদের সেনাসংখ্যাও রাশিয়ার তুলনায় অনেক কম। ইউক্রেন বাহিনীর পাশাপাশি কিয়েভের বর্তমান পশ্চিমাপন্থী নেতৃত্বও রাশিয়ার বিরুদ্ধে লড়াই করতে আগ্রহী নন। কারণ, তাঁরা ভালো করেই জানেন মস্কোর বিরুদ্ধে লড়াইয়ে তাঁরা জয়ী হতে পারবেন না। আর এ রকম হলে ইউক্রেনের অন্যান্য অঞ্চল দখল করে নেওয়াটা মস্কোর জন্য কোনো ব্যাপারই নয়। তবে রাশিয়ার এই আগ্রাসন আন্তর্জাতিক মহলে ব্যাপক নিন্দা কুড়িয়েছে। যুক্তরাষ্ট্র ও তার মিত্ররা ইউক্রেনে সামরিক হস্তক্ষেপ না করতে মস্কোর প্রতি আহ্বান জানিয়েছে। মার্কিন প্রেসিডেন্ট বারাক ওবামা নানাভাবে চেষ্টা চালিয়ে যাচ্ছেন যাতে রাশিয়া ক্রিমিয়া থেকে সেনা সরিয়ে নেয়। সঙ্গে আছে জি-৮ থেকে বহিষ্কার, অর্থনৈতিক অবরোধ আরোপসহ নানা হুমকি। সব মিলিয়ে ওবামা নরম-গরমে রাশিয়ার মত পাল্টানোর চেষ্টা করছেন। কিন্তু পুতিনকে টলানো যাচ্ছে না। তাঁর পাল্টা হুঁশিয়ারি, জি-৮-এর সদস্যপদ খোয়ানোর আশঙ্কা বা অর্থনৈতিক অবরোধের হুমকি দিয়ে রাশিয়াকে পর্যুদস্ত করা যাবে না। রাশিয়াকে চাপে ফেললে বাকিদেরও অর্থনৈতিক দিক থেকে ভুগতে হবে। পুতিনের এ কথাটা তাঁর পশ্চিমা ‘শত্রু’রা জানেন না তা নয়। এর আগে ইরান ও সিরিয়ার ব্যাপারে প্রেসিডেন্ট পুতিনের কঠোর অবস্থানের কারণে যুক্তরাষ্ট্র ইচ্ছা থাকলেও সহিংস হতে পারেনি, বরং এখনো আরব বিশ্বের এই দুটি দেশের সঙ্গে আলাপ-আলোচনার মাধ্যমে সমস্যা সমাধানের চেষ্টা করছে। এ ক্ষেত্রে রাশিয়া চীনকেও পাশে পেয়েছে। ফলে যুক্তরাষ্ট্রের কথায় যে বিশ্ব চলে না তা প্রমাণিত হয়েছে। এদিকে সর্বশেষ পাওয়া খবরে জানা গেছে, গতকাল বেলজিয়ামের ব্রাসেলসে ইউক্রেন বিষয়ে জাতিসংঘের নিরাপত্তা পরিষদ ও ইউরোপীয় ইউনিয়নের নেতাদের এক জরুরি বৈঠক হওয়ার কথা। এ ছাড়া ব্রাসেলসে ন্যাটো ও রাশিয়ার মধ্যেও এক বৈঠক হওয়ার কথা রয়েছে। ফ্রান্সের রাজধানী প্যারিসে গত বুধবার বৈঠক করেন মার্কিন পররাষ্ট্রমন্ত্রী জন কেরি ও রুশ পররাষ্ট্রমন্ত্রী সের্গেই লাভরভ। এতসব বৈঠক ও আলোচনা রাশিয়ার মত পাল্টাতে পারে কি না তা এখন দেখার বিষয়। সূত্র, সিএনএন, রয়টার্স ও এএফপি','অনলাইন প্রতিবেদক','11','2018-02-16 17:02:29.223941','2018-02-16 17:02:29.224003');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('3','ইউক্রেনে রুশ পদক্ষেপে ওবামার উদ্বেগ','','মার্কিন প্রেসিডেন্ট বারাক ওবামামার্কিন প্রেসিডেন্ট বারাক ওবামাইউক্রেনে রাশিয়ার পদক্ষেপ নিয়ে মার্কিন প্রেসিডেন্ট বারাক ওবামা এবং ইউরোপীয় ইউনিয়নভুক্ত তাদের মিত্র দেশগুলো ‘গভীর উদ্বেগ’ প্রকাশ করেছে। একই সঙ্গে ওয়াশিংটন হুঁশিয়ারি দিয়ে বলেছে, ইউক্রেনে রাশিয়া সামরিক শক্তি জোরদার করলে সংকটের কূটনৈতিক সমাধানের সম্ভাবনা পুরোপুরি ভেস্তে যাবে। আজ রোববার এএফপির এক প্রতিবেদনে এ তথ্য জানানো হয়।  হোয়াইট হাউস জানায়, ইউক্রেন-সংকট নিয়ে বারাক ওবামা ফ্রান্সের প্রেসিডেন্ট ফ্রাঁসোয়া ওলাঁদ, যুক্তরাজ্যের প্রধানমন্ত্রী ডেভিড ক্যামেরন এবং ইতালির প্রধানমন্ত্রী মাত্তেও রেনসির সঙ্গে ফোনালাপ করেছেন। এই নেতারা রাশিয়া স্পষ্টভাবে আন্তর্জাতিক আইন ভঙ্গ করেছে উল্লেখ করে গভীর উদ্বেগ প্রকাশ করেছেন। একই সঙ্গে ইউক্রেনের সার্বভৌমত্ব ও আঞ্চলিক অখণ্ডতা বজায় রাখতে তাঁদের সমর্থন পুনর্ব্যক্ত করেন। ওবামা একই সঙ্গে ব্যালটিক দেশ হিসেবে পরিচিত এস্তোনিয়া, লাটভিয়া, লিথুনিয়ার নেতাদের সঙ্গেও ফোনে এ সংকট নিয়ে আলোচনা করেন।  ক্যামেরনের ডাউনিং স্ট্রিট কার্যালয় জানায়, ক্রিমিয়ার ঘাঁটি থেকে রুশ সেনা ফিরিয়ে নেওয়া এবং মে মাসে ইউক্রেনে নির্বাচনে সমর্থন দিয়ে রাশিয়ার পক্ষে এখনো এ সংকট সমাধানের সুযোগ রয়েছে বলে মনে করেন যুক্তরাজ্যের প্রধানমন্ত্রী ও ওবামা।   এদিকে ইউক্রেন-সংকটকে কেন্দ্র করে রাশিয়ার বিরুদ্ধে ‘চটজলদি ও বেপরোয়া পদক্ষেপ’ না নিতে যুক্তরাষ্ট্রকে সতর্ক করে দিয়েছে রাশিয়া। একই সঙ্গে ইউক্রেনে গ্যাস সরবরাহ বন্ধের হুমকি দিয়েছে ক্রেমলিন। ইউক্রেনের জ্বালানি খাত অনেকাংশেই রাশিয়ার ওপর নির্ভরশীল। শুক্রবার রাশিয়ার রাষ্ট্রীয় মালিকানাধীন অন্যতম বৃহত্ গ্যাস কোম্পানি গ্যাজপ্রম ওই হুমকি দেয়।  গত বৃহস্পতিবার ওবামা ইউক্রেনে আগ্রাসনের জন্য দায়ী রুশ নেতাদের ভিসা বন্ধ ও যুক্তরাষ্ট্রে তাঁদের সম্পদ জব্দ করার নির্দেশ দেন। তবে মার্কিন এক কর্মকর্তা জানান, রাশিয়ার এসব নেতার তালিকায় পুতিনের নাম নেই। যুক্তরাষ্ট্রের এই পদক্ষেপের বিরুদ্ধে ক্ষুব্ধ প্রতিক্রিয়া জানিয়েছে রাশিয়া।  এদিকে ইউক্রেনের ভারপ্রাপ্ত পররাষ্ট্রমন্ত্রী গতকাল রাজধানী কিয়েভে সংবাদ সম্মেলনে বলেন, ‘ক্রিমিয়া ইউক্রেনের আছে এবং থাকবে। আমরা কখনোই এটা অন্য কারও হাতে তুলে দেব না।’  ক্রিমিয়ায় ২০ হাজার রুশ সেনা?  মার্কিন প্রতিরক্ষা বিভাগের সদর দপ্তর পেন্টাগনের হিসাব অনুযায়ী, ইউক্রেনের ক্রিমিয়ায় ২০ হাজার রুশ সেনা অবস্থান করছে; যদিও ইউক্রেনের সীমান্তরক্ষী বাহিনীর প্রধান বলছেন, ক্রিমিয়ায় রুশ সেনার সংখ্যা ৩০ হাজার। তবে রুশ প্রেসিডেন্ট পুতিন বলছেন, ক্রিমিয়ায় থাকা সশস্ত্র ব্যক্তিরা স্থানীয় ‘প্রতিরক্ষা বাহিনী’র সদস্য। ওই বাহিনীর ওপর রাশিয়ার নিয়ন্ত্রণ নেই।','অনলাইন প্রতিবেদক','10','2018-02-16 17:03:26.252518','2018-02-16 17:03:26.252577');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('4','ছবির সূত্র ধরেও ধ্বংসাবশেষ মিলল না','','চীনের স্যাটেলাইট থেকে পাওয়া ‘সন্দেহজনক ধ্বংসাবশেষ’-এর স্থানটিতে কোনো কিছুর অস্তিত্ব খুঁজে পায়নি মালয়েশিয়া। মালয়েশিয়ার বেসামরিক বিমান চলাচল কর্তৃপক্ষের প্রধান আজহারউদ্দিন আবদুল রহমান আজ বৃহস্পতিবার এ দাবি করেন।  আবদুল রহমান কুয়ালালামপুরে সাংবাদিকদের বলেন, ‘সেখানে কিছু নেই। আমরা সেখানে গিয়েছিলাম। কিছু পাওয়া যায়নি।’  এপির বরাত দিয়ে টাইমস অব ইন্ডিয়ার খবরে বলা হয়, এর আগে ভিয়েতনাম কর্তৃপক্ষও ওই অঞ্চলটি ব্যাপকভাবে তল্লাশি চালিয়েছিল বলে জানায়।  চীনের অ্যাডমিনিস্ট্রেশন ফর সায়েন্স, টেকনোলজি অ্যান্ড ইন্ডাস্ট্রি ফর ন্যাশনাল ডিফেন্সের বরাত দিয়ে বিভিন্ন সংবাদমাধ্যম জানায়, মালয়েশিয়ার নিখোঁজ বিমানটির সন্ধানে নিয়োজিত চীনের একটি স্যাটেলাইট সাগরে ‘সন্দেহজনক ধ্বংসাবশেষ’ শনাক্ত করেছে। সংস্থাটি গতকাল বুধবার কয়েকটি ছবিও প্রকাশ করেছে। সংস্থাটি বলছে, তাদের উপগ্রহ থেকে তোলা ছবিতে সাগরে ভাসমান তিনটি সন্দেহজনক ধ্বংসাবশেষের সন্ধান মিলেছে। এই ধ্বংসাবশেষগুলোর ধরন এবং সেগুলোর আকারই নিখোঁজ বিমানটির সন্ধানে গুরুত্বপূর্ণ সূত্র হিসেবে কাজ করতে পারে। এর একটির আকার ১৩–১৮ মিটার, আরেকটির আকার ১৪–১৯ মিটার, অন্যটির ২৪–২২ মিটার।  তবে চীনের পার্লামেন্টের বার্ষিক সভার ফাঁকে আজ বেসামরিক বিমান চলাচল কর্তৃপক্ষের প্রধান লি জিয়াঝিয়াং সাংবাদিকদের বলেন, স্যাটেলাইটের মাধ্যমে পাওয়া দক্ষিণ চীন সাগরে ভাসমান ‘কিছু বস্তুর’ ছবি যে মালয়েশিয়ার নিখোঁজ উড়োজাহাজের, এর কোনো প্রমাণ নেই। তিনি বলেন, ‘চীনের স্যাটেলাইট কিছু ধোঁয়া ও ভাসমান বস্তুর ছবি পেয়েছে। আমরা এখনই নিশ্চিত করতে পারছি না, এগুলো নিখোঁজ বিমানটির কি না।’ তিনি বলেন, মালয়েশিয়ার কর্তৃপক্ষ নিখোঁজ উড়োজাহাজটির বিষয়ে কোনো কিছু গোপন করছে না।  এদিকে বিন্দু পরিমাণ আশা জিইয়ে থাকা পর্যন্ত মালয়েশিয়ার নিখোঁজ বিমানটির অনুসন্ধান চালিয়ে যাবে বলে জানিয়েছেন চীনের প্রধানমন্ত্রী লি কেকিয়াং। আজ চীনের পার্লামেন্টের বার্ষিক অধিবেশন শেষে সংবাদ সম্মেলনে প্রধানমন্ত্রী বলেন, বিমান নিখোঁজের বিষয়ে পাওয়া যেকোনো তথ্যই যাচাই করে দেখা হবে। বহু দেশের অংশগ্রহণে বড় পরিসরে নিখোঁজ বিমানটির সন্ধান তত্পরতা চলছে।  গত শুক্রবার মধ্যরাতে মোট ১৪টি দেশের ২৩৯ জন আরোহী নিয়ে কুয়ালালামপুর থেকে চীনের বেইজিংগামী ফ্লাইট এমএইচ৩৭০ নিখোঁজ হয়। মালয়েশিয়া এয়ারলাইনসের প্রকাশিত তালিকা থেকে দেখা যায়, উড়োজাহাজে সবচেয়ে বেশি ১৫৩ জন আরোহী ছিলেন চীনের।  ভিয়েতনামের দক্ষিণে জলসীমায় যাওয়ার পর রহস্যজনকভাবে বোয়িং ৭৭৭-২০০ইআর উড়োজাহাজটি নিখোঁজ হয়। নিখোঁজ হওয়ার পর এই ছয় দিনে ২২টি বিমান ও ৪০টি বিভিন্ন ধরনের নৌযান তন্ন তন্ন করে খুঁজছে উড়োজাহাজটিকে। গত মঙ্গলবার চীন ১০টি স্যাটেলাইট মোতায়েন করে।','অনলাইন প্রতিবেদক','7','2018-02-16 17:04:18.061023','2018-02-16 17:04:18.061076');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('5','জেনেভা আলোচনাই রক্তপাত বন্ধের পথ: বান কি মুন','','জেনেভা শান্তি আলোচনার মাধ্যমে রক্তপাত বন্ধের উপায় খোঁজাই সিরিয়ায় সহিংসতা বন্ধের সবচেয়ে ভালো পথ বলে মনে করেন জাতিসংঘের মহাসচিব বান কি মুন। তাই জেনেভা আলোচনায় ফিরতে সংশ্লিষ্ট সব পক্ষের প্রতি আহ্বান জানিয়েছেন তিনি। বান কি মুনের মুখপাত্র মার্টিন নেসির্কি গত মঙ্গলবার বলেন, মহাসচিব এখনো আশাবাদী, শান্তি সম্মেলন এগিয়ে যাবে। তিনি আশা করছেন, সব পক্ষই যত তাড়াতাড়ি সম্ভব আলোচনার টেবিলে ফিরে এসে আলোচনার প্রক্রিয়া এগিয়ে নেবে। নেসির্কি জানান, বান কি মুন মনে করেন, এটা এমন একটি বিষয়, যা দু-একটি বৈঠকের মাধ্যমেই সমাধানে পৌঁছানো সম্ভব নয়...এ প্রক্রিয়ার জন্য দীর্ঘ সময় প্রয়োজন। জাতিসংঘের দূত লাখদার ব্রাহিমি এই প্রক্রিয়া নিয়ে এগিয়ে যাবেন বলে আশাবাদী তিনি। নেসির্কি জানান, ব্রাহিমি শিগগিরই নিউইয়র্কে বান কি মুনের সঙ্গে সাক্ষাৎ করে জেনেভা আলোচনার বর্তমান পরিস্থিতি তুলে ধরবেন। এএফপি।','অনলাইন প্রতিবেদক','9','2018-02-16 17:05:19.496179','2018-02-16 17:05:19.496233');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('6','ঢাকা আসছেন মাহাথির মোহাম্মদ','','আধুনিক মালয়েশিয়ার রূপকার ও সাবেক প্রধানমন্ত্রী মাহাথির বিন মোহাম্মদ আগামী শনিবার সকালে ঢাকায় আসবেন। ইউনিভার্সিটি অব ইনফরমেশন টেকনোলজি অ্যান্ড সায়েন্সেসের (ইউআইটিএস) দ্বিতীয় সমাবর্তনে যোগ দিতে তিনি আসছেন।  আগামী শনিবার মাহাথির মোহাম্মদ বঙ্গবন্ধু আন্তর্জাতিক সম্মেলন কেন্দ্রে ইউআইটিএসের সমাবর্তনে ‘সমাবর্তন বক্তা’ হিসেবে উপস্থিত থাকবেন। পরদিন রোববার তিনি ঢাকা ছাড়বেন।  আজ ঢাকা রিপোর্টার্স ইউনিটিতে সংবাদ সম্মেলনে ইউআইটিএসের উপাচার্য মোহাম্মদ সামাদ এ তথ্য জানান। সমাবর্তনে মোট ছয় হাজার ৮১ জন শিক্ষার্থীকে সনদ দেওয়া হবে। এর মধ্যে ১৫ জন কৃতী শিক্ষার্থীকে স্বর্ণপদক দেওয়া হবে। সমাবর্তন অনুষ্ঠানে সভাপতিত্ব করবেন বিশ্ববিদ্যালয়ের চ্যান্সেলর ও রাষ্ট্রপতি মো. আবদুল হামিদ।  সংবাদ সম্মেলনে অন্যদের মধ্যে বক্তব্য দেন বিশ্ববিদ্যালয়ের ট্রাস্টি বোর্ডের চেয়ারম্যান সূফী মোহাম্মদ মিজানুর রহমান, সহ-উপাচার্য কে এম সাইফুল ইসলাম খান। আরও উপস্থিত ছিলেন দিল্লিতে অবস্থিত হজরত নিজামুদ্দিন আউলিয়া মাজারের পীর নাজিম আল নিজামী।','অনলাইন প্রতিবেদক','9','2018-02-16 17:06:10.668613','2018-02-16 17:06:10.668676');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('7','আমি মারা যাচ্ছি','','গলায় গুলি লাগার পর প্রবল বেগে রক্ত পড়ছিল ক্ষতস্থান থেকে। সেই সময় ‘আমি মারা যাচ্ছি’ লিখে খুদে ব্লগ লেখার ওয়েবসাইট টুইটারে বার্তা দিয়েছিলেন স্বেচ্ছাসেবী ওলিসিয়া জুকোভস্কা। মুহূর্তেই তা ছড়িয়ে পড়ে বিভিন্ন সামাজিক যোগাযোগমাধ্যমে। ছবি দেখে অনেকেই ধারণা করেন, ওলিসিয়া আর নেই। তবে শেষ পর্যন্ত বেঁচে গেছেন ২১ বছরের এই তরুণী।
ইউক্রেনের রাজধানী কিয়েভের ইনডিপেনডেন্স স্কয়ারে সরকারবিরোধী বিক্ষোভে গত বুধ ও বৃহস্পতিবার যে সহিংসতা চলে, তার প্রতীক ওলিসিয়ার ওই ছবি। ওই দুদিন সহিংসতায় অন্তত ৭৭ জন নিহত হন। ওলিসিয়াও যোগ দিয়েছিলেন বিক্ষোভে। বিক্ষোভে কেউ অসুস্থ হলে তাঁদের সেবা দিতে স্বেচ্ছাসেবী হিসেবে কাজ করছিলেন তিনি। বৃহস্পতিবার বিক্ষোভের একপর্যায়ে তাঁর গলায় গুলি লাগে। কিয়েভের একটি হাসপাতালে ভর্তি করা হয় তাঁকে।
হাসপাতালের বিছানায় শুয়ে বার্তা সংস্থাকে ওলিসিয়া বলেন, ‘আমি সামাজিক ও বাক্স্বাধীনতার জন্য, গণতন্ত্রের জন্য, স্বাভাবিক জীবনযাপনের নিশ্চয়তার জন্য বিক্ষোভে যোগ দিয়েছিলাম। আমি রাজনীতি করি না। কোনো রাজনৈতিক দলের সদস্য নই। শুধু জনগণের সঙ্গে একাত্ম হতেই ইনডিপেনডেন্স স্কয়ারে গিয়েছি।’
টুইটারে বার্তা দেওয়া প্রসঙ্গে ওলিসিয়া বলেন, ‘আমাকে অ্যাম্বুলেন্সে তোলার পর নিজের ফোন খুঁজছিলাম। দুই হাত রক্তে ভেসে গেছে। ফোন হাতে নিয়ে “আমি মারা যাচ্ছি” লিখে ছবিসহ পোস্ট করেছি। চিকিৎসক তা দেখে আমার কাছ থেকে ফোন সরিয়ে নেন।’ এপি।','অনলাইন প্রতিবেদক','11','2018-02-16 17:07:02.303599','2018-02-16 17:07:02.303648');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('8','আসন্ন অর্থবছরের বাজেট প্রায় আড়াই লাখ কোটি টাকা','','আসন্ন অর্থবছরের বাজেট প্রায় আড়াই লাখ কোটি টাকা ঢাকা, ২৭ মার্চ, এবিনিউজ : চলতি অর্থবছরের সংশোধিত বাজেটের আকার নির্ধারণ করা হয়েছে ২ লাখ ২২ হাজার ৪৯১ কোটি টাকা। তবে নতুন অর্থবছরের বাজেট হতে পারে ২ লাখ ৪০ হাজার কোটি থেকে ৪৫ হাজার কোটি টাকা। তাছাড়া বাজেটে পর্যটন খাতকে বিশেষ গুরুত্ব দেয়া হচ্ছে। এ হিসাবে এটি চলতি অর্থবছরের চেয়ে প্রায় ১৩ শতাংশ বেশি। আজ বৃহস্পতিবার সচিবালয়ে ২০১৪-১৫ অর্থবছরের প্রথম প্রাকবাজেট বৈঠকে অর্থমন্ত্রী আবুল মাল আব্দুল মুহিত একথা বলেন।
এদিকে সচিবালয়ে আজকের প্রাকবাজেট বৈঠকে বাংলাদেশ উন্নয়ন গবেষণা প্রতিষ্ঠান (বিআইডিএস), বাংলাদেশ অর্থনীতি সমিতি, ইকোনোমিক রিসার্চ গ্রুপ (ইআরজি) এবং পলিসি রিসার্চ ইনস্টিটিউটের (পিআরআই) প্রতিনিধিরা অংশ নেন। বাজেটে বর্তমানের মতো কৃষি, মানবসম্পদ, বিদ্যুৎ ও জ্বালানি এবং অবকাঠামো খাতকে অগ্রাধিকার দেয়া হবে বলে অর্থমন্ত্রী জানান।
অন্যদিকে আগামী ৫ জুন বর্তমান সরকারের প্রথম এবং তার অষ্টম বাজেট পেশ করবেন অর্থমন্ত্রী আবুল মাল আব্দুল মুহিত। মে মাসের প্রথম সপ্তাহ পর্যন্ত এ মতবিনিময় চলবে। সেগুলো পর্যালোচনা করে অর্থমন্ত্রী জাতীয় সংসদে বাজেট পেশ করবেন। আলোচনার পরে জুনের শেষের দিকে বাজেট পাস হবে।','অনলাইন প্রতিবেদক','110','2018-02-16 17:07:50.912240','2018-02-16 17:07:50.912297');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('9','র‌্যাব দেশ ও জনগণের স্বার্থেই কাজ করে : শিল্পমন্ত্রী','','র‌্যাব দেশ ও জনগণের স্বার্থেই কাজ করে : শিল্পমন্ত্রীঢাকা, ২৭ মার্চ, এবিনিউজ : র‌্যাব যা করে তা দেশ ও জনগণের স্বার্থেই করে। বিগত দশ বছরে র‌্যাবের কার্যক্রম ভূয়সী প্রশংসার দাবি রাখে। বিশেষ করে জঙ্গিবাদ দমন, মাদকদ্র্যব, অপহরণ, গুম ইত্যাদি প্রতিরোধে র‌্যাব সফলতার সাথে কাজ করছে। তবে কিছু স্বার্থান্বেষী মহল র‌্যাবের এ অর্জনকে ম্লান করার জন্য বিভিন্ন অপপ্রচার চালিয়ে যাচ্ছে। আজ বৃহস্পতিবার র‌্যাপিড অ্যাকশন ব্যাটেলিয়ন র‌্যাব সদর দফতরে র‌্যাবের দশম প্রতিষ্ঠাবার্ষিকী উপলক্ষে আয়োজিত অনুষ্ঠানে শিল্পমন্ত্রী আমির হোসেন আমু সাংবাদিকদের এসব কথা বলেন।
এদিকে শিল্পমন্ত্রী সাংবাদিক দম্পতি সাগর-রুনি হত্যাকাণ্ড সম্পর্কে বলেন, সাগর-রুনি হত্যাকাণ্ডের রহস্য উদঘাটনে আন্তরিকতার সাথেই কাজ করছে র‌্যাব। তদন্ত অব্যাহত আছে। অল্পদিনের মধ্যেই রহস্য উদঘাটন করে র‌্যাব গণমাধ্যমকে জানাবে।
স¤প্রতি প্রিজন ভ্যান থেকে মৃত্যুদণ্ডপ্রাপ্ত জেএমবি সদস্যদের ছিনিয়ে নেয়া প্রসঙ্গে শিল্পমন্ত্রী বলেন, পালিয়ে যাওয়া ২ জঙ্গিতে দ্রুত গ্রেফতার করা হবে।','অনলাইন প্রতিবেদক','10','2018-02-16 17:08:33.032416','2018-02-16 17:08:33.032466');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('10','ওয়েঙ্গারকে রোবেনের জবাব','','আর্সেন ওয়েঙ্গার আর আরিয়েন রোবেনের ঠোকাঠুকিটা শুরু হয়েছিল চ্যাম্পিয়নস লিগের শেষ ষোলোর প্রথম লেগেই। ২-০ গোলে হারের পর রোবেনকে ‘ডাইভার’ বলে বিদ্রূপ করেছিলেন ওয়েঙ্গার। সে সময় অবশ্য চুপই ছিলেন ডাচ উইঙ্গার। দ্বিতীয় লেগের খেলা শেষেও একই অভিযোগ শোনার পর বেশ ভালোই ক্ষেপে গেছেন রোবেন। কড়া ভাষায় জবাব দিয়েছেন আর্সেনাল কোচের সমালোচনার।

গত মাসে প্রথম লেগের খেলায় রোবেনকে ফাউল করে লাল কার্ড দেখেছিলেন আর্সেনাল গোলরক্ষক শেজনি। ৩৭ মিনিটের পর আর্সেনাল পরিণত হয়েছিল ১০ জনের দলে। নিজেদের মাঠে ২-০ গোলে হারের পর ক্ষুব্ধ ওয়েঙ্গার কড়া সমালোচনা করেছিলেন রোবেনের। ফিরতি লেগের খেলা শেষেও ওয়েঙ্গার মুখর হয়েছেন রোবেনের সমালোচনায়। ফরাসি এই কোচের ভাষায়, ‘রোবেন খুবই ভালো খেলোয়াড় আর খুবই ভালো ডাইভার।’ বারবার একই ধরনের খোঁচা খেয়েই বোধ হয় আর চুপ করে থাকেননি রোবেন। পাল্টা তোপ দেগেছেন ওয়েঙ্গারের দিকে, ‘আমি সব সময়ই বলি যে, যদি আপনি একজন বড় কোচ হন, তাহলে হারটা মেনে নিতে হবে। যদি জিতে যান তাহলে খুশি হবেন, সেটা উপভোগ করবেন। কিন্তু হারের পর তুচ্ছ বিষয় নিয়ে অভিযোগ শুরু করবেন না। হেরে যাওয়ার পর বড় একজন কোচের কাছ থেকে আপনি এর চেয়ে বেশি কিছু আশা করবেন।’

শেষ বাঁশি বাজার কিছুক্ষণ আগে পেনাল্টি আদায় করে নিয়েছিলেন রোবেন। তবে পেনাল্টি থেকে গোল করে দলকে জেতানোর সহজ সুযোগটা কাজে লাগাতে পারেননি থমাস মুলার। ১-১ গোলের ড্র নিয়েই মাঠ ছাড়তে হয়েছে বায়ার্ন মিউনিখকে। জয় না পেলেও অবশ্য কোনো ক্ষতি হয়নি গতবারের শিরোপাজয়ীদের। দুই লেগ মিলিয়ে ৩-১ গোলের জয় দিয়ে ঠিকই চলে গেছে চ্যাম্পিয়নস লিগের কোয়ার্টার ফাইনালে।','অনলাইন প্রতিবেদক','2','2018-02-16 17:11:53.944330','2018-02-16 17:11:53.944383');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('11','নতুন চরিত্রে মাহিয়া মাহি','','শুরু থেকেই ভিন্ন ভিন্ন মেকাপ গেটাপে দর্শকের মনোরঞ্জন করে আসছেন হাল সময়ের সবচেয়ে আলোচিত নায়িকা মাহিয়া মাহি। সময়ের সঙ্গে সঙ্গে যেন সেই ধারাবাহিকতাই বজায় রেখে পথ চলছেন তিনি। পরপর ৩টি রোমান্টিক সিনেমা দিয়ে নিজের পথ সুগম করলেও প্রতিটি সিনেমাতেই যেন নিত্য নতুন মাহির দেখা মিলেছে। তারপর শাকিব খানের বিপরীতে ভালোবাসা আজকাল সিনেমাতেও মাহিকে দেখা গেছে নতুন সাজে। বাপ্পির সঙ্গে তবুও ভালোবাসি এবং কী দারুণ দেখতে  সিনেমাতেও দর্শককে আকৃষ্ট করেছেন ব্যতিক্রমী উপস্থাপনা দিয়ে। এর পর পরই অগ্নি ঝড়। অগ্নির আলোচনা শেষ হতে না হতেই ‘দবির সাহেবের সংসার’। এবার প্রথমবারের মতো কমেডি ছবিতে অভিনয় করছেন মাহি। জাকির হোসেন রাজু পরিচালিত এ ছবিটি আগামী ৪ এপ্রিল মুক্তি পাচ্ছে শতাধিক সিনেমাহলে। অগ্নির মতো দবির সাহেবের সংসারও মুক্তির আগেই সাঁড়া ফেলেছে ইন্টারনেটে। বিশেষ করে ফেসবুকে রীতিমতো হই চই পড়ে গেছে বলা চলে। এখানে কাজের মেয়ের চরিত্রে অভিনয় করেছেন। বিপরীতে রয়েছেন বাপ্পি এবং  নবাগত রোজ। 
একের পর এক সাফল্যে উচ্ছ্বাস প্রকাশ করে মাহি বলেন, ‘নিজেকে ভাগ্যবতী ছাড়া আর কিছুই বলতে পারছি না। আমার প্রত্যাশার চেয়েও যেন  অনেক বেশি ভালোবাসা পাচ্ছি দর্শকের। এতো ভালোবাসার পরেও মনে হচ্ছে দবির সাহেবের সংসার সিনেমাটি নতুনমাত্রার এক বিনোদন দিবে দর্শককে। এখানে সম্পূর্ণ নতুন আঙ্গিকে দর্শক আমাকে দেখতে পারবে। মাহি আরো বলেন, শুধু গল্প কিংবা লোকেশন নয়, সিনেমার গানেও রয়েছে ব্যতিক্রমী ছোয়া। সবমিলিয়ে প্রত্যাশার পরিমাণ অনেক বেশি। 
জাজ মাল্টিমিডিয়া প্রযোজিত ছবিতে নাম ভুমিকায় অভিনয় করেছেন আলীরাজ। এছাড়া আরো অভিনয় করেছেন আলীরাজ, আলেকজান্ডার, আসিফ ইমরোজ, রেবেকা প্রমুখ।','অনলাইন প্রতিবেদক','90','2018-02-16 17:19:33.727446','2018-02-16 17:19:33.727499');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('12','মুক্তিযুদ্ধের চেতনাকে তুলির রঙে রাঙিয়েছেন বরেণ্য ও নবীনশিল্পীরা','','মুক্তিযুদ্ধের চেতনাকে তুলির রঙে রাঙিয়েছেন বরেণ্য ও নবীনশিল্পীরাঢাকা, ২৬ মার্চ, এবিনিউজ : চ্যানেল আই আয়োজন করেছিল মহান স্বাধীনতা দিবস উপলক্ষে ৫ম বারের মতো ‘রংতুলিতে মুক্তিযুদ্ধ’। এ অনুষ্ঠানেই মুক্তিযুদ্ধের চেতনাকে তুলির রঙে রাঙিয়েছেন সৈয়দ শামসুল হক, হাশেম খান-সহ অর্ধশত বরেণ্য ও নবীন চিত্রশিল্পীরা। সেসঙ্গে আরো একবার জাগ্রত হলো মহান মুক্তিযুদ্ধের চেতনা। বিকেল ৩.০৫ মিনিটে আফজাল হোসেনের উপস্থাপনায় রেজওয়ানা চৌধুরী বন্যার নেতৃত্বে সুরের ধারার শিল্পীদের সঙ্গীত পরিবেশনের মধ্যে দিয়ে অনুষ্ঠানটি শুরু হয়। স্বাগত বক্তব্য রাখেন চ্যানেল আইর ব্যবস্থাপনা পরিচালক ফরিদুর রেজা সাগর। এ সময় উপস্থিত ছিলেন মেজর (অব.) কামরুল ইসলাম ভূঁইয়া, সৈয়দ শামসুল হক, হাশেম খান, ফজলে খোদা, আনোয়ারা সৈয়দ হক, কবি হাবিবুল্লাহ সিরাজী-সহ দেশের অর্ধশত প্রবীণ ও নবীন চিত্রশিল্পীবৃন্দ এবং বিশিষ্ট ব্যক্তিগণ। রংতুলিতে মুক্তিযুদ্ধ এবার পৃষ্ঠপোষকতা করেছে আইএফআইসি ব্যাংক। এই প্রথম দেশের কোনো টিভি চ্যানেল ড্রোন ক্যামেরার সাহায্যে অনুষ্ঠানের কিছু কিছু অংশ সরাসরি স¤প্রচার করেছে।
মুক্তিযুদ্ধের চিত্রাংকনের পাশাপাশি মূল মঞ্চে দেশের গান পরিবেশন করেন সুরের ধারার শিল্পীরা। দেশপ্রেম ও মুক্তিযুদ্ধ বিষয়ে চিত্রাংকন করেছেন সমরজিৎ রায় চৌধুরী, শহীদ কবীর, জাহিদ মুস্তাফা, আবদুল মান্নান, সৈয়দ ইকবাল, রেজাউল করিম, শেখ আফজাল, নাসিম আহমেদ নদভী, শাহজাহান আহমেদ, রেজাউন নবী, সোহানা শাহরীন, আলপ্তগীন তুষার, ফারজানা আহমেদ উর্মী, সৈয়দ জাহিদ ইকবাল, অনুকুল চন্দ্র মজুমদার, শেখ জহুর, মোঃ জহির উদ্দিন, হামিদুজ্জামান খান, গোলাম রব্বানী শামীম, বিজয় সেন, শারমীন জামান, মোবাশ্বের আলম, কেরামত মাওলা, আব্দুস সাত্তার তৌহিদ, লায়লা আঞ্জুমান আরা প্রমুখ। এ ছাড়া চিত্রাংকনে অংশ নিয়েছে অর্ধশত ক্ষুদে আঁকিয়ে।
কবিতা আবৃত্তি করেন জয়ন্ত চট্টোপাধ্যায়, আফরোজা বানু প্রমুখ। অনুষ্ঠানটি প্রযোজনা করেছেন আমীরুল ইসলাম ও শহিদুল আলম সাচ্চু। বেলা ৩.০৫ মিনিট থেকে অনুষ্ঠানটি চ্যানেল আই সরাসরি স¤প্রচার করেছে। বরগুনা সদর উপজেলা নির্বাচন','অনলাইন প্রতিবেদক','10','2018-02-16 17:20:23.910365','2018-02-16 17:20:23.910428');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('13','এবার ধুম ফোরে ভিলেন সালমান খান','','এবার ধুম ফোরে ভিলেন সালমান খানমুম্বাই, ২৭ মার্চ, এবিনিউজ : আবার আসছে যশরাজ ফিল্মসের একশন সিরিজ ধুম ৷ ধুম, ধুম টু, ধুম থ্রি-এর পর এবার রূপালী পর্দায় আসবে ধুম ফোর। তবে শুধু নতুন ধুম নয়, চমক রয়েছে সবদিকে। বলিউড সূত্রে জানা যায়, নতুন ধুম ছবিতে অভিনয় করবেন দাবাং তারকা সালমান খান। তবে তিনি  ভিলেনের চরিত্রে অভিনয় করবেন।
প্রথম ধুম থেকেই পরিচালকেরা একটি নিয়ম করে ফেলেছিলেন, বলিউডের গৎবাঁধা নায়কদের ইমেজ ভেঙে ধুম ছবিতে তারাই হবে ছবির ভিলেন। তবে ধুম ভিলেন কিন্তু শেষমেশ হয়ে উঠতেন ছবির নায়ক। প্রথম ধুম-এর জন আব্রাহাম থেকে হৃত্বিক, এবং ধুম থ্রিয়ের আমির এ ব্যাপারে সবাই একাই একশো। এই নামজাদা নায়ক ভিলেনদের পাশে অবশ্য থাকতেন ছবির নায়ক অভিষেক বচ্চন। কিন্তু সব ধুমেতেই অভিষেককে টেক্কা দিয়ে বেরিয়ে যেতেন ছবির ভিলেন।
সেই নিয়ম মেনেই ধুম ফোরেও ভিলেন হতে চলেছেন সালমান খান। জানাযায়, সালমানকে দেখা যাবে একেবারে নতুন লুকে। এমনকি ছবিতে ফিরে আসতে পারেন হৃত্বিক রোশনও।','অনলাইন প্রতিবেদক','10','2018-02-16 17:21:36.499359','2018-02-16 17:21:36.499424');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('14','৩ এপ্রিল জাতীয় চলচ্চিত্র দিবস পালনের প্রস্তুতি সম্পন্ন','','৩ এপ্রিল জাতীয় চলচ্চিত্র দিবস পালনের প্রস্তুতি সম্পন্ন ঢাকা, ২৭ মার্চ, এবিনিউজ : বর্ণাঢ্য আয়োজনে আগামী ৩ এপ্রিল জাতীয় চলচ্চিত্র দিবস পালনের প্রস্তুতি নিয়েছে বাংলাদেশ চলচ্চিত্র উন্নয়ন কর্পোরেশন (বিএফডিসি)। এ উপলক্ষে আজ বৃহস্পতিবার এফডিসির জহির রায়হান কালার ল্যাবে অনুষ্ঠানের পৃষ্ঠপোষক ডায়মন্ড ওয়ার্ল্ড ও মিডিয়া পার্টনার এটিএন বাংলার মধ্যে সমঝোতা স্মারক স্বাক্ষর করা হয়। অনুষ্ঠানে উপস্থিত ছিলেন জাতীয় চলচ্চিত্র দিবস উদযাপন আয়োজনের আহবায়ক নায়ক রাজ রাজ্জাক, এফডিসির ব্যবস্থাপনা পরিচালক পীযুষ বন্দোপাধ্যায়, ডায়মন্ড ওয়ার্ল্ডের কর্ণধার দিলীপ আগারওয়াল, এটিএন বাংলার পরিচালক মনিরুল ইসলাম ও রুকসানা কবীর কাকলী, উপদেষ্টা (বিক্রয় ও বিপনন) এম শামসুল হুদা, উপদেষ্টা (অনুষ্ঠান) নওয়াজীশ আলী খান, উপদেষ্টা (প্রশাসন ও মানবসম্পদ) মীর মোঃ মোতাহার হাসান, ইভেন্ট ম্যানেজমেন্ট সংস্থা পথিকের সিইও তুহিন বড়–য়াসহ চলচ্চিত্র প্রযোজক ও পরিচালকবৃন্ধ।  
এটিএন বাংলা ও এফডিসির মধ্যে সমঝোতা স্মারক স্বাক্ষর করেন এটিএন বাংলার পরিচালক মনিরুল ইসলাম ও এফডিসির ব্যবস্থাপনা পরিচালক পীযুষ বন্দোপাধ্যায়। এফডিসি ও ডায়মন্ড ওয়ার্ল্ডের মধ্যে সমঝোতা স্মারক স্বাক্ষর করেন এফডিসির ব্যবস্থাপনা পরিচালক পীযুষ বন্দোপাধ্যায় ও ডায়মন্ড ওয়ার্ল্ডের কর্ণধার দিলীপ আগারওয়াল। 
আগামী ৩ এপ্রিল সকাল ১০ টা থেকে রাত ১১টা পর্যন্ত চলচ্চিত্র দিবস পালনের দিনব্যাপী নানা কর্মসূচী গ্রহণ করা হয়েছে। সকাল ১০টায় বেলুন ও পায়রা উড়িয়ে র‌্যালীর মধ্যে দিয়ে আয়োজনের সূচনা হবে। মেলা, রেড কার্পেটে তারকাদের আনাগোনা, টক শো, স্থির চিত্র প্রদর্শনী, সেমিনার, চলচ্চিত্র নির্মাণ বিষয়ক প্রজেকশন সেমিনার, চলচ্চিত্র প্রদর্শন থাকবে এই বিশেষ আয়োজনে। সন্ধ্যায় রয়েছে চলচ্চিত্র তারকাদের অংশগ্রহণে মনোজ্ঞ সাংস্কৃতিক অনুষ্ঠান। সবশেষে হাতিরঝিলে আতশবাজি ও ফানুস উড়িয়ে চলচ্চিত্র দিবসের সমাপ্তি ঘোষণা করা হবে। পুরো আয়োজনটি এটিএন বাংলা সকাল ১০টা থেকে রাত ১০টা পর্যন্ত সরাসরি স¤প্রচার করবে। 
চলচ্চিত্র সংশ্লিষ্ট সকল সংগঠনের সহযোগিতায় এ দিনটি পালন করা হবে। পুরো অনুষ্ঠানটির ইভেন্টের দায়িত্ব পালন করবে পথিক।','অনলাইন প্রতিবেদক','10','2018-02-16 17:22:23.989011','2018-02-16 17:22:23.989060');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('15','সানিকে দেশ থেকে তাড়ানোর দাবি','','সানিকে দেশ থেকে তাড়ানোর দাবি জনজাগৃতি সমিতিরঢাকা, ২৭ মার্চ, এবিনিউজ : রাগিনী এমএমএস টু মুক্তি পেতেই বিপাকে পড়লেন সানি লিওন। হিন্দু জনজাগৃতি সমিতির রোষে পড়ে গিয়েছেন সানি। শুধু ছবির ওপর নিষেধাজ্ঞা জারি করেই থেমে থাকেনি হিন্দু সংগঠনটি। সানিকে দেশ থেকে বিতাড়িত করতেও উঠে পড়ে লেগেছে।
রাগিনী এমএমএস টু ছবিতে হনুমান চলিসার ব্যবহার নিয়ে সেন্সর বোর্ডকে চিঠি লিখেছে হিন্দু সংগঠন। চিঠিতে বলা হয়েছে, কুখ্যাত পর্নস্টার সানি এই ছবিতে মুখ্য ভূমিকায় অভিনয় করেছেন। শ্রী হনুমান এমন একজন যাঁর নিজের অঙ্গ, চরিত্রের ওপর সম্পূর্ণ নিয়ন্ত্রণ ছিল। যাঁর সঙ্গে হিন্দুত্বের আবেগ জড়িয়ে রয়েছে, সেই হনুমানজীর নাম নিয়ে ভারতীয় ভাবাবেগে আঘাত করা হয়েছে। তাই হিন্দু জনজাগৃতি সমিতি তাই ছবির ওপর নিষেধাজ্ঞা দাবি করেছে। সানি ভারতের নতুন প্রজন্মের রুচিকে বিকৃত করছে। ওকে অবিলম্বে ভারত থেকে বিতাড়িত করা উচিত্।','অনলাইন প্রতিবেদক','2','2018-02-16 17:23:39.060937','2018-02-16 17:23:39.060989');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('16','ডাচদের লক্ষ্য ১৪৬ রান','','শ্রীলঙ্কার বিপক্ষে দুঃস্বপ্নের ম্যাচের খোলস থেকে বেরিয়ে এসেছে নেদারল্যান্ডস। তারা সুপার টেন পর্বের গ্রুপ ওয়ানের খেলায় শক্তিশালী দক্ষিণ আফ্রিকাকে নির্ধারিত ওভারে ৯ উইকেটে মাত্র ১৪৫ রানে বেঁধে রাখে। ফলে জয়ের জন্য তাদের লক্ষ্য এখন ১৪৬ রান। 
এবার ইনফর্ম ব্যাটসম্যান জেপি ডুমিনিকে হারালো দক্ষিণ আফ্রিকা। ইনিংসের ১৬তম ওভারে ১৫ বলে ১২ রানের দীরগতির ইনিংস খেলে তিনি ফন ডার গটেনের বলে কট বিহাইন্ড হন। এ সময় দলীয় সংগ্রহ ছিল ১৬ ওভার শেষে ৬ উইকেটে ১২০ রান। 
এর আগে স্পিনার আহসান মালিক আবারো আঘাত হানেন। ইনিংসের ১৩তম ওভারে তিনি ফেরান হার্ডহিটার অ্যালবি মরকেলকে। ফলে মাত্র ১০২ রানে ৫ উইকেট হারিয়ে চাপের মুখে দক্ষিণ আফ্রিকা। তার আগে স্বল্প সময়ের ব্যবধানে ২ সেট ব্যাটসম্যানকে হারিয়ে চাপের মুখে পড়ে দক্ষিণ আফ্রিকা। এবার ফন বিক তুলে নেন প্রোটিয়াদের অন্যতম সেরা ব্যাটসম্যান এবি ডিভিলিয়ার্সকে। ইনিংসের ১১তম ওভারের প্রথম বলে ২১ বলে ২১ রান করে ডিভিলিয়ার্স ক্যাচ তুলে দেন বেন কুপারের হাতে। এ সময় দলীয় সংগ্রহ ৪ উইকেটে ৯২ রান।
এর আগে অধিনায়ক ফাফ ডু প্লেসিসকে ফন বিকের তালুবন্দী করেন টম কুপার। মাত্র ১৪ বলে ৩ চার ও ১ ছক্কায় ২৪ রান করে প্রোটিয়া অধিনায়ক। এ সময় ৮.৫ ওভারে দলীয় সংগ্রহ ছিল ৩ উইকেটে ৮৪ রান।
ইনিংসের পঞ্চম ওভারে দ্বিতীয় সাফল্য পেল নেদারল্যান্ডস। আহসান মালিক এবার ফেরালেন নির্ভর যোগ্য হাশিম আমলাকে। মাত্র ২২ বলে ৭ চার ও ১ ছক্কায় ৪৩ রানের অনবদ্য ইনিংস খেলা আমলা উইকেটরক্ষক বারেসির গ্লাভসবন্দী হন। দলীয় সংগ্রহ ছিল তখন ৪.৩ ওভারে ২ উইকেটে ৪৫ রান। শেষ খবর, ৮ ওভার শেষে দলীয় সংগ্রহ ২ উইকেটে ৭৫ রান।','অনলাইন প্রতিবেদক','1','2018-02-16 17:28:18.041356','2018-02-16 17:28:18.041405');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('17','শিরোপা লড়াইয়ে বার্সা : গোল পেয়েছেন মেসি ও নেইমার','','দুই তারকার নৈপুণ্যে সেল্তা ভিগোকে ৩-০ গোলে হারিয়ে লা লিগার শিরোপা লড়াইয়ে দাপটের সাথে টিকে আছে বার্সেলোনা। তবে বড় একটি দুশ্চিন্তা নিয়ে মাঠ ছাড়তে হয়েছে বার্সাকে। ম্যাচের ২৩ মিনিটে হাঁটুতে চোট পেয়ে স্ট্রেচারে করে মাঠ ছাড়েন গোলরক্ষক ভিক্তর ভালদেস। 
পরে ক্লাবের ওয়েবসাইটে জানানো হয়, সুস্থ হয়ে আবারো মাঠে ফিরতে তার অস্ত্রোপচারের প্রয়োজন। তাই মৌসুমের বাকি সময় তো বটেই, বিশ্বকাপেও খেলতে পারবেন না তিনি।
দুই দিন আগেই চিরপ্রতিদ্বন্দ্বী রিয়াল মাদ্রিদকে তাদেরই মাঠে ৪-৩ গোলে হারানোর আকাশচুম্বি আত্মবিশ্বাস নিয়ে ক্যাম্প নউতে বুধবার রাতে খেলতে নেমেছিল বার্সা। দুর্বল সেল্তা ভিগোকে হারাতে তেমন একটা সমস্যা হয়নি স্পেনের অন্যতম সফল দলটির।
ষষ্ঠ মিনিটে আক্রমণভাগের ৩ খেলোয়াড়ের সংঘবদ্ধ দারুণ এক আক্রমণে এগিয়ে যায় গতবারের চ্যাম্পিয়নরা। মেসির কাছ থেকে পাস পেয়ে নেইমারের দিকে বল বাড়ান চিলির স্ট্রাইকার আলেক্সিস সানচেস। আর তা থেকেই লক্ষ্যভেদ করেন নেইমার।
বার্সার একচেটিয়া আক্রমণের মাঝে ২০ মিনিটে খেলার চিত্রপটে সামান্য ভিন্নতা আসে। সেল্তার স্ট্রাইকার ফাবিয়ান ওরেলানের একটি শট বার্সার ডিফেন্ডার বারত্রা হাত দিয়ে ঠেকালে পেনাল্টির বাঁশি বাজান রেফারি। পরে অবশ্য লাইন্সম্যান জানায়, ডি বক্সের ঠিক বাইরে থেকে বলটি ঠেকিয়েছিল বারত্রা। ফলে পেনাল্টির বদলে ফ্রি-কিক পায় সেল্তা। তবে সাফল্য পায়নি তারা।
তবে এর ৭ মিনিট বাদে সব দুশ্চিন্তা কাটিয়ে ব্যবধান দ্বিগুণ করেন মেসি। অভিজ্ঞ মিডফিল্ডার আন্দ্রেস ইনিয়েস্তার পাস থেকে দারুণ দক্ষতায় প্রতিপক্ষের গোলরক্ষককে কাটিয়ে জালে বল পাঠান চারবারের বর্ষসেরা এই ফুটবলার।
দ্বিতীয়ার্ধে অবশ্য অতিথিরা ৫৩ ও ৫৪তম মিনিটে ২টি দারুণ সুযোগ পেয়েছিল। কিন্তু ভালদেসের বদলে নামা হোসে মানুয়েল পিন্তোরর অসাধারণ নৈপুণ্য বাঁচিয়ে দেয় বার্সেলোনাকে।
তবে সেল্তার ম্যাচে ফেরার সব আশা শেষ হয়ে যায় ৬৭ মিনিটে। সানচেসের বাড়ানো বল পেয়ে দুই ডিফেন্ডারকে ফাঁকি দিয়ে গোল করে সহজ জয় নিশ্চিত করেন নেইমার।
৮৭ মিনিটে ব্যবধান বাড়ানোর আরেকটি সুযোগ পেয়েছিল বার্সেলোনা। কিন্তু মিডফিল্ডার স্যার্হি রবের্তোর প্রচেষ্টা ব্যর্থ করে দেন সেল্তার গোলরক্ষক।
এই জয়ের ফলে ক্ষণিকের জন্য হলেও শীর্ষে উঠেছিল বার্সেলোনা। তবে নেইমার-মেসিদের জয়ের কিছুক্ষণ পর গ্রানাদাকে হারিয়ে শীর্ষস্থান পুনরুদ্ধার করে আতলেতিকো মাদ্রিদ। ৩০ ম্যাচে আতলেতিকোর পয়েন্ট ৭৩ আর সমান ম্যাচে বার্সেলোনার পয়েন্ট ৭২।
তবে একই রাতে হোঁচট খেয়েছে রিয়াল মাদ্রিদ। সেভিয়ার মাঠে ২-১ গোলে হেরে সমান ম্যাচে ৭০ পয়েন্ট নিয়ে তৃতীয় স্থানে নেমে গেছে তারা।','অনলাইন প্রতিবেদক','11','2018-02-16 17:29:02.251878','2018-02-16 17:29:02.251925');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('18','গাভাস্কার বিসিসিআই সভাপতি হতে প্রস্তুত','','ভারতীয় ক্রিকেট বোর্ডের (বিসিসিআই) সভাপতি হতে প্রস্তুত সাবেক অধিনায়ক সুনিল গাভাস্কার। আজ বৃহস্পতিবার তিনি এনডিটিভিকে বলেন, দেশের সর্বোচ্চ আদালতের কোন নির্দেশ পালন করতে পারলে তিনি খুশি হবেন। এর আগে দেশটির সুপ্রিম কোর্ট শ্রীনিবাসনের পরিবর্তে বিসিসিআইর অন্তর্র্বতীকালীন সভাপতি হিসাবে গাভাস্কারের নাম উল্লেখ করে এমন কাউকে নিয়োগ দেয়ার প্রস্তাব দেন। যদিও তাৎক্ষণিকভাবে এ বিষয়ে কোন মন্তব্য করা তার জন্য কঠিন বলে স্বীকার করেন তিনি। তবে বিখ্যাত এ সাবেক ক্রিকেটার এ ধরনের কোন প্রস্তাব পেলে দায়িত্ব গ্রহণে প্রস্তুত বলে ইঙ্গিত দেন। 
গাভাস্কার বলেন, একজন ওপেনিং ব্যাটসম্যান হিসাবে অবশ্যই আপনাকে সকল প্রকার চ্যালেঞ্জ মোকাবেলা করার জন্য প্রস্তুত থাকতে হবে। আমার সামর্থ্যে কুলায় এমন সব কিছুই আমি করব। দেশের সর্বোচ্চ আদালত যেহেতু আমার ওপর এ বিশ্বাস রেখেছে, তাদের নির্দেশমত সব কিছু করতে পারলে আমি খুশি হব।
৬৪ বছর বয়সী গাভাস্কার বেশ সতর্ক উচ্চারণ করেন এবং বলেন, শ্রীনিবাসনকে এখনই দোষী বলা যাবে না। তিনি বলেন, দোষী প্রমাণিত না হওয়া পর্যন্ত শ্রীনিবাসন নির্দোষ। তবে যেহেতু আমি আইনের লোক নই তাই সুপ্রিম কোর্ট কি মনে করেছে আমি সে বিষয়ে কোন মন্তব্য করতে পারি না। এর আগে সুপ্রিম কোর্ট জানিয়েছিল আইপিএল স্পট ফিক্সিং এবং বেটিং বিষয়ে শুক্রবার আদেশ দেয়া হবে। কিন্তু শ্রীনিবাসনের পরিবর্তে অন্তর্র্বতীকালীন প্রধান হিসাবে আজ গাভাস্কারের নাম প্রস্তাব করে।','অনলাইন প্রতিবেদক','20','2018-02-16 17:29:50.267026','2018-02-16 17:29:50.267079');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('19','ব্যাট হাতে দক্ষিণ আফ্রিকার সংগ্রহ : ৯২/৪','','আইসিসি টি২০ বিশ্বকাপের সুপার টেন পর্বের গ্রুপ ওয়ান থেকে নিজেদের অস্তিত্ব রক্ষার ম্যাচে টস হেরে প্রথমে ব্যাট করার সুযোগ পেয়েছে দক্ষিণ আফ্রিকা। ব্যাট করতে নেমেই অবশ্য ইনিংসের ৫ম বলে তারা হারায় ওপেনার কুইন্টন ডি কককে। তিনি মিচেল স্টোয়ার্টের বলে শূন্য রানে স্টিফান মেবার্ঘের তালুবন্দী হন দলীয় ৩ রানের মাথায়। এর পর আর ৩ জন সাজ ঘরে ফিরে গেছেন।
এ প্রতিবেদন লেখার সময় দক্ষিণ আফ্রিকা ১০ ওভার শেষে ৪ উইকেটে ৯২ রান সংগ্রহ করেছে।
মাঠে আছেন, এলবি মরকেল এবং অপরপ্রান্তে জেপি ডুমিনি ।

চট্টগ্রামের জহুর আহমেদ চৌধুরী স্টেডিয়ামে চলমান গ্রুপ ওয়ানের এ খেলায় টস জিতে নেদারল্যান্ডসের অধিনায়ক পিটার বোরেন প্রথমে প্রতিপক্ষকে ব্যাট করার আমন্ত্রণ জানান। সুপার টেন পর্বে নিজেদের প্রথম ম্যাচে শ্রীলঙ্কার বিরুদ্ধে মাত্র ৩৯ রানে অল আউট হওয়া ডাচরা এ ম্যাচে ঘুরে দাঁড়াতে চায়। অন্যদিকে প্রথম ম্যাচে শ্রীলঙ্কার কাছে হেরে যাওয়া প্রোটিয়ারা এ ম্যাচে জয়ের পাশাপাশি নিজেদের রান রেট মজবুত করার দিকেও গুরুত্ব দিচ্ছে।

দক্ষিণ আফ্রিকা: কুইন্টন ডি কক(উইকেটরক্ষক), হাশিম আমলা, ফাফ ডু প্লেসিস(অধিনায়ক), এবি ডিভিলিয়ার্স, জেপি ডুমিনি, ডেভিড মিলার, এলবি মরকেল, ডেল স্টেইন, বেউরন হেনড্রিকস, ইমরান তাহির, লনওয়াবো তোতসোবে।

নেদারল্যান্ডস: স্টিফান মেবার্ঘ, মিচেল স্টোয়ার্ফ, ওয়েসলি বারেসি(উইকেটরক্ষক), টম কুপার, বেন কুপার, পিটার বোরেন(অধিনায়ক), মুদাসসর বুখারি, লোগান ফন বিক, ফন ডার গটেন, পিটার সিলার, আহসান মালিক।','অনলাইন প্রতিবেদক','10','2018-02-16 17:30:36.365307','2018-02-16 17:30:36.365354');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('20','ডু অর ডাই ম্যাচে আগামীকাল ওইন্ডিজ- অস্ট্রেলিয়া মুখোমুখি','','টি২০ বিশ্ব কাপে ‘ডেথ গ্রুপ’ হিসাবে পরিচিত দুই নম্বর গ্রুপ। দুদিন বিরতির পর সুপার টেন পর্বে আগামীকাল শুক্রবার ডু অর ডাই ম্যাচে মুখোমুখি হচ্ছে বর্তমান চ্যাম্পিয়ন ওয়েষ্ট ইন্ডিজ এবং অস্ট্রেলিয়া। পর পর দু’টি ম্যাচে জয়ী হয়ে ডেথ গ্রুপের শীর্ষে অবস্থান করছে ভারত। আর অস্ট্রেলিয়া, পাকিস্তান, ওয়েষ্ট ইন্ডিজ এবং স্বাগতিক বাংলাদেশ প্রত্যেকেই একটি করে ম্যাচে পরাজিত হয়েছে। প্রতিটি গ্রুপ থেকে শীর্ষ দু’টি দল সেমিফাইনাল খেলবে। তবে চার ম্যাচের ম্যধ্যে তিনটি জয় পেলেই কোন দলের সেমিফাইনাল নিশ্চিত হবে না। তবে দু’টি পরাজয় টুর্নামেন্ট থেকে ছিটকে পড়া অনেকটাই নিশ্চিত করবে।
৪ বার ৫০ ওভারের বিশ্ব কাপ জয় করা অস্ট্রেলিয়া দলের অধিনায়ক প্রথমবারের মত টি২০ শিরোপা জয়ের জন্য জন্য উন্মুখ। যদিও টুর্নামেন্টে এ পর্যন্ত তাদের একমাত্র ম্যাচে অসিরা পাকিস্তানের কাছে পরাজিত হয়েছে। বর্তমান চ্যাম্পিয়ন ওয়েষ্ট ইন্ডিজ তাদের প্রথম ম্যাচে ভারতের কাছে পরাজিত হওয়ার পর দ্বিতীয় ম্যাচে মঙ্গলবার স্বাগতিক বাংলাদেশকে ৭৩ রানে হারিয়ে ঘুরে দাঁড়িয়েছে। এ জয়ের ফলে গ্রুপের সেরা ১.৭৫ নেট রান রেটে এগিয়ে রয়েছে দলটি।
অস্ট্রেলিয়ার হাতে এখনো তিন ম্যাচ বাকি আছে, ওয়েষ্ট ইন্ডিজের হাতে আছে দুটি। আগামীকাল শেরেবাংলা স্টেডিয়ামে ডাল হেডার ম্যাচের প্রথম খেলায় কোন দলই পরাজয় হজম করতে পারবে না। অস্ট্রেলিয়া উইকেটরক্ষক ব্র্যাড হাডিন বলেন, নক আউট অবস্থাটা কখনো কখনো তাদের জন্য আশীর্বাদ হয়ে দাঁড়াতে পারে। কখনো কখনো এমন অবস্থা চাপ কমিয়ে দেয় কেন না আপনার অবস্থান কোথায় আপনি সেটা জানেন।
হাডিন বলেন, আমাদের সামনে দ্বিতীয় কোন সুুযোগ নেই। আমাদের সেরা খেলাটা আমাদের নিশ্চিত করতে হবে এবং চাপের মধ্যে ভাল খেলতে হবে। গত চার মাসে আমরা এটা বেশ ভালভাবে করেছি এবং আবারো করতে না পারার আমি কোন কারণ দেখছি না। অস্ট্রেলিয়ার বিপক্ষে এটা অত্যন্ত কঠিন ম্যাচ হবে স্বীকার করেন ওয়েস্ট ইন্ডিজ অধিনায়ক ড্যারেন সামি।
সামি বলেন, অস্ট্রেলিয়া একটি ভাল দল, সুতরাং এটা কোন সহজ ম্যাচে হবে না আমরা জানি। তবে এটা আরেকটা নতুন ক্রিকেট ম্যাচ- ব্যাট-বলের প্রতিযোগিতা। সাধারণত চাপ ভালভাবে সামলানো দলই জয়ী হয়। টি২০ বিশ্ব কাপের ইতিহাসে সবচেয়ে বয়স্ক খেলোয়াড় ৪৩ বছর বয়সী স্পিনার ব্র্যাড হগকে এ ম্যাচে বাদ দিতে পারে অস্ট্রেলিয়া। কেননা পাকিস্তানের বিপক্ষে তার পারফরমেন্স ছিল খুবই খারাপ।
আগের ম্যাচে ৫৪ বলে সর্বোচ্চ ৯৪ রান করা পাকিস্তানী ব্যটসম্যান উমর আকমল ব্যক্তিগত ২২ রানে জীবন পান হগের ক্যাচ মিসের কারণে। এরপর নবম ওভারে বোলিং করতে এসে তিন ওভারে ২৯ রান দিয়েও কোন উইকেট নিতে পারেননি হগ। হগের পরিবর্তে দলে সুযোগ পেতে পারেন স্পিনার ২০ বছর বয়সী জেমস মুইরহেড। এছাড়া হাঁটুুর ইনজুরি কাটিয়ে দলে ফিরতে পারেন অলরাউন্ডার জেমস ফকনার। পক্ষান্তরে ওয়েষ্ট ইন্ডিজের ব্যাটিং লাইন আপ এখনো থিতু হতে পারেনি। যদিও বাংলাদেশের বিপক্ষে ডোয়াইন স্মিথ ৪৩ বলে ৭২ এবং দলের তারকা ব্যাটসম্যান ক্রিস গেইল ৪৮ বলে করেছেন ৪৮ রান।
তবে দুই স্পিনার স্যামুয়েল বদ্রি ও সুনিল নারাইন এবং পেসার ক্রিসমার স্যান্টোকি বেশ ভাল বোলিং করেছেন। সামি বলেন, প্রত্যেক ম্যাচেই প্রত্যেকের সেরা খেলাটা খেলা উচিত। আমরা এখনো আমাদের সেরাটা খেলেছি বলে আমি মনে করি না। দিনের দ্বিতীয় ম্যাচে মহেন্দ্র সিং ধোনির ভারতীয় দল স্বাগতিক বাংলাদেশের মোকাবেলা করবে। পাকিস্তান এবং ওয়েষ্ট ইন্ডিজের বিপক্ষে উভয় ম্যাচেই সাত উইকেটে জয় পাওয়া ভারত রয়েছে বেশ ফুরফুরে মেজাজে। সহজেই এ ম্যাচে জয়ের আশা করছে ভারত। তবে প্রথম ম্যাচে খুবই খারাপ করার পর নিজেদের মান-সম্মান বাঁচাতে ভাল খেলতে ঙ্গীকারাবদ্ধ বাংলাদেশ।','অনলাইন প্রতিবেদক','11','2018-02-16 17:31:39.201684','2018-02-16 17:31:39.201736');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('21','৫০ বছরের ছোট প্রেমিকাকে বিয়ে করছেন বেরলুসকোনি','','ইতালির সাবেক প্রধানমন্ত্রী সিলভিও বেরলুসকোনির সঙ্গে তাঁর স্ত্রীর আনুষ্ঠানিক বিবাহবিচ্ছেদ হয়েছে। যৌন কেলেঙ্কারিসহ নানা কারণে বিতর্কিত এই কোটিপতি এবার তাঁর চেয়ে প্রায় ৫০ বছরের ছোট প্রেমিকাকে বিয়ের পরিকল্পনা করছেন।
ইতালির বিভিন্ন গণমাধ্যমে প্রকাশিত প্রতিবেদনে দাবি করা হয়েছে, সম্প্রতি ভেরোনিকা লারিওর সঙ্গে বেরলুসকোনির আনুষ্ঠানিক ছাড়াছাড়ি সম্পন্ন হয়েছে। এর ফলে তৃতীয় বিয়ের পথ সুগম হয়েছে।
৭৭ বছর বয়সী বেরলুসকোনি এখন ২৯ বছর বয়সী বাদামি চুলের প্রেমিকা ফ্রসেসকা প্যাসকেলকে বিয়ে করবেন বলে ঠিক করেছেন। ওই তরুণী ইতিমধ্যে বিয়ের পোশাকও কিনে ফেলেছেন। 
লারিওর সঙ্গে ২২ বছর সংসার করেছেন বেরলুসকোনি। চার বছর থেকেছেন আলাদা। ওই সংসারে তাঁদের তিন সন্তান রয়েছে। এএফপি।','অনলাইন প্রতিবেদক','10','2018-02-16 17:32:26.498647','2018-02-16 17:32:26.498713');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('22','গণজাগরণ মঞ্চ কর্তৃক জাতীয় পতাকা পদদলিত : ঢাবিতে মানববন্ধন','','ঢাবিতে মানববন্ধনঢাবি, ২৭ মার্চ, এবিনিউজ : ‘গণজাগরণ মঞ্চ’ কর্তৃক জাতীয় পতাকা পদদলিত করার প্রতিবাদে ও দায়ী ব্যক্তিদের শাস্তির দাবিতে মানববন্ধন করেছে ঢাকা বিশ্ববিদ্যালয়ের সচেতন শিক্ষার্থীরা। আজ বৃহস্পতিবার দুপুর ১২টার দিকে ঢাকা বিশ্ববিদ্যালয়ের অপরাজেয় বাংলার পাদদেশে ‘ঢাকা বিশ্ববিদ্যালয়ের সচেতন শিক্ষার্থীবৃন্দ’ ব্যানারে মানববন্ধন ও মৌন মিছিল অনুষ্ঠিত হয়।
প্রায় দেড়শতাধিক শিক্ষার্থীদের অংশগ্রহণের মানববন্ধন শেষে একটি মৌন মিছিল ক্যাম্পাসের গুরুত্বপূর্ণ সড়ক ঘুরে শাহবাগ জাতীয় জাদুঘরের সামনে পাঁচ মিনিট নিরবতা পালনের মধ্য দিয়ে এই প্রতিবাদ কর্মসূচী শেষ হয়।
প্রতিবাদ কর্মসূচীর ব্যানারের একপাশে লেখা ছিল ‘প্রাণের জাতীয় পতাকার কনসেপ্ট ব্যবহার করে মঞ্চ বানিয়ে লাল-সবুজকে পদদলিত করলো গণজাগরণ মঞ্চ। রুখে দাঁড়াও ছাত্রসমাজ’। অন্যপাশে ছিলো ‘জাতীয় পতাকার লাল-সবুজকে পদদলিত করার প্রতিবাদে এবং দায়ী ব্যক্তিদের শাস্তির দাবিতে মানবন্ধন’।
উল্লেখ্য, গতকাল বুধবার স্বাধীনতা দিবসে শাহবাগে ‘যুদ্ধপরাধের বিচারের ও গণজাগরণ মঞ্চের ৬ দফা: আমরা কোথায়?’ শীর্ষক এক পথসংলাপের আয়োজন করে গণজাগরণ মঞ্চ। আর এসব আয়োজনের জন্য শাহববাগে লাল-সবুজে রঙে জাতীয় পতাকার আদলে একটি মঞ্চ বানানো হয়। যার ওপরে দাঁড়িয়ে বক্তব্য দেন গণজাগরণ মঞ্চের নেতাকর্মীরা।','অনলাইন প্রতিবেদক','10','2018-02-16 17:33:22.878733','2018-02-16 17:41:35.431650');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('23','উন্মুক্ত বিশ্ববিদ্যালয় এসএসসির ফল প্রকাশ : পাস ৭৩ শতাংশ','','উন্মুক্ত বিশ্ববিদ্যালয়ের অধীনে অনুষ্ঠিত এসএসসি পরীক্ষা-২০১২ এর ফল প্রকাশিত হয়েছে। আজ বৃহস্পতিবার দুপুরে শিক্ষা মন্ত্রণালয়ে আয়োজিত সংবাদ সম্মেলনে শিক্ষামন্ত্রী নুরুল ইসলাম নাহিদ ফলের বিস্তারিত তুলে ধরেন। এতে গড় পাসের হার ৭২ দশমিক ৯৭ ভাগ।
চূড়ান্ত পরীক্ষায় পাস করেছে ৪৪ হাজার ৭৭৩ জন। এর মধ্যে ছাত্র ২৫ হাজার ১৭৮ জন এবং ছাত্রী ১৯ হাজার ৫৯৫ জন।
এর মধ্যে জিপিএ-৫ পেয়েছে ২৭ জন, জিপিএ-৪ পেয়েছে ৩ হাজার ৫৬১ জন এবং জিপিএ-৩.৫ পেয়েছে ১০ হাজার ১৪৭ জন।','অনলাইন প্রতিবেদক','11','2018-02-16 17:34:13.593735','2018-02-16 17:40:43.341225');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('24','ঢাবি উপাচার্যের সঙ্গে ২জাপানি অধ্যাপকের সাক্ষাৎ','','জাপানের ইওয়াতে বিশ্ববিদ্যালয়ের অধ্যাপক ড. মাতসু উইমুরা এবং সহযোগী অধ্যাপক  ড. আবিদুর রহমান আজ ঢাকা বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. আ আ ম স আরেফিন সিদ্দিকের সঙ্গে তাঁর অফিসে সাক্ষাৎ করেছেন। 
সাক্ষাৎকালে তাঁরা পারস্পরিক স্বার্থ-সংশ্লিষ্ট বিষয়ে বিশেষ করে ঢাকা বিশ্ববিদ্যালয় এবং জাপানের ইওয়াতে বিশ্ববিদ্যালয়ের মধ্যে কৃষি বিজ্ঞান, জীববিজ্ঞান এবং প্রাণ রসায়ন ও অণুপ্রাণ বিজ্ঞান বিষয়ে যৌথ পিএইচ.ডি. প্রোগ্রাম চালুর সম্ভাব্যতা নিয়ে আলোচনা করেন। এ বিষয়ে তারা উভয় প্রতিষ্ঠানের মধ্যে শিগগিরই একটি সমঝোতা স্মারক স্বাক্ষরের ব্যাপারে মত বিনিময় করেন।
এসময় ঢাকা বিশ্ববিদ্যালয় জীববিজ্ঞান অনুষদের ডিন অধ্যাপক ড. মো: ইমদাদুল হক উপস্থিত ছিলেন।  
উপাচার্য অধ্যাপক ড. আ আ ম স আরেফিন সিদ্দিক ঢাকা বিশ্ববিদ্যালয়ে আসা এবং এ বিশ্ববিদ্যালয়ের শিক্ষা কার্যক্রমের প্রতি গভীর আগ্রহ প্রকাশের জন্য অতিথিদের ধন্যবাদ জানান।','অনলাইন প্রতিবেদক','10','2018-02-16 17:34:54.241057','2018-02-16 17:42:15.006434');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('25','ঢাবি কম্পিউটার সায়েন্স এন্ড ইঞ্জিনিয়ারিং বিভাগের নবীন বরণ ও বিদায় সংবর্ধনা অনুষ্ঠিত','','ঢাকা বিশ্ববিদ্যালয় কম্পিউটার সায়েন্স এন্ড ইঞ্জিনিয়ারিং বিভাগের ২০তম ব্যাচের শিক্ষার্থীদের নবীন বরণ ও ১৩তম ব্যাচের শিক্ষার্থীদের বিদায় সংবর্ধনা অনুষ্ঠান আজ বৃহস্পতিবার টিএসসি মিলনায়তনে অনুষ্ঠিত হয়। 
বিভাগের চেয়ারম্যান অধ্যাপক ড. মো: হাসানুজ্জামানের সভাপতিত্বে অনুষ্ঠানে উপাচার্য অধ্যাপক ড. আ আ ম স আরেফিন সিদ্দিক প্রধান অতিথি হিসাবে উপস্থিত ছিলেন। অনুষ্ঠানে অন্যান্যের মধ্যে বক্তব্য রাখেন প্রো-উপাচার্য (প্রশাসন) অধ্যাপক ড. সহিদ আকতার হুসাইন, কোষাধ্যক্ষ অধ্যাপক ড. মো: কামাল উদ্দীন, ইঞ্জিনিয়ারিং এন্ড টেকনোলজি অনুষদের ডিন অধ্যাপক ড. মো: সেকুল ইসলাম, বিভাগের প্রতিষ্ঠাতা চেয়ারম্যান অধ্যাপক ড. এম লুৎফর রহমান এবং বিভাগীয় ছাত্র উপদেষ্টা ড. সাইফুদ্দিন মো: তারিক। 
উপাচার্য অধ্যাপক ড. আ আ ম স আরেফিন সিদ্দিক নবীন শিক্ষার্থীদের বিশ্ববিদ্যালয়ে স্বাগত জানিয়ে এবং বিদায়ী শিক্ষার্থীদের অভিনন্দন জানিয়ে বলেন, পৃথিবী যেমন বৈচিত্রময়, তেমনি বিশ্ববিদ্যালয় জীবনও বৈচিত্রময়। বিশ্ববিদ্যালয় একটি জ্ঞানবৃক্ষ। এর অনেক শাখা-প্রশাখা আছে। শিক্ষার্থীরা বিশ্ববিদ্যালয় জীবনে জ্ঞান-বিজ্ঞানের সকল শাখা-প্রশাখায় বিচরণ করে নিজেদের গড়ে তুলবে বলে তিনি আশা প্রকাশ করেন। 
কম্পিউটার সায়েন্স এন্ড ইঞ্জিনিয়ারিং বিভাগকে ঢাকা বিশ্ববিদ্যালয়ের একটি ঐতিহ্যবাহী ও গুরুত্বপূর্ণ বিভাগ আখ্যায়িত করে উপাচার্য বলেন, বর্তমান তথ্য প্রযুক্তির যুগে কম্পিউটারের জ্ঞান ছাড়া শিক্ষার্থীরা পূর্ণাঙ্গ জ্ঞান লাভ করতে পারে না। ঢাকা বিশ্ববিদ্যালয় হচ্ছে মুক্তবুদ্ধি চর্চার স্থান। এখানে রয়েছে নানান সুযোগ-সুবিধা। এই সুযোগ-সুবিধার সদ্ব্যবহার করে শিক্ষার্থীদের সৃজনশীলতার বিকাশ ঘটাতে হবে।
অনুষ্ঠানের আগে উপাচার্য অধ্যাপক ড. আ আ ম স আরেফিন সিদ্দিক কম্পিউটার সায়েন্স এন্ড ইঞ্জিনিয়ারিং বিভাগের জন্য নির্মিতব্য সায়েন্স কমপ্লেক্স ভবনের ৭ম তলা নির্মাণ কাজের ভিত্তি প্রস্তর স্থাপন করেন। দেড় কোটি টাকা ব্যয়ে ১০ হাজার বর্গফুটের নির্মিতব্য এই ফ্লোরে তিনটি অত্যাধুনিক ল্যাব, দুইটি ক্লাসরুম, একটি অত্যাধুনিক কনফারেন্স রুম এবং সাতটি শিক্ষক রুম থাকবে।','অনলাইন প্রতিবেদক','10','2018-02-16 17:35:50.690246','2018-02-16 17:42:53.709235');
INSERT INTO "news_news" ("id","headline","image_file","content","reporter","hits","created_at","updated_at") VALUES ('26','বিশ্ববিদ্যালয়ের দাবিতে ২০ মার্চ ইডেনে ধর্মঘট','','প্রধানমন্ত্রীকে বেঁধে দেওয়া সময় শেষ হলেও দাবি পূরণের আশ্বাস না পাওয়ায় আবারও আন্দোলনে নেমেছেন ইডেন মহিলা কলেজের আন্দোলনরত শিক্ষার্থীরা। একই সঙ্গে তাঁরা ২০ মার্চ কলেজে ধর্মঘট ডেকেছেন বেলা ১১টা থেকে দুপুর ১২টা।

ইডেন মহিলা কলেজকে স্বায়ত্তশাসিত বিশ্ববিদ্যালয় করার দাবিতে ২ মার্চ একটি প্রতিনিধিদল প্রধানমন্ত্রীর কার্যালয়ে স্মারকলিপি দেন। স্মারকলিপি দেওয়ার আগে তাঁরা এক সমাবেশ থেকে ১০ দিনের সময় বেঁধে দেন।

আজ বৃহস্পতিবার বেলা ১১টা থেকে কলেজের মূল ফটকের সামনের রাস্তায় ঘণ্টাব্যাপী বিক্ষোভ সমাবেশ করেন আন্দোলনরত শিক্ষার্থীরা।

শিক্ষার্থীরা বলেন, প্রধানমন্ত্রীর কাছে স্মারকলিপি পেশ করার ১০ দিন পেরিয়ে গেলেও এখনো তাঁর কোনো সাড়া পাওয়া যায়নি। তাই তাঁরা আবারও মাঠে নেমেছেন। দাবি পূরণ না হওয়া পর্যন্ত আন্দোলন চালিয়ে যাওয়ার ঘোষণা দেন শিক্ষার্থীরা। সমাবেশ শেষে তাঁরা একটি মিছিল বের করেন। মিছিলটি নীলক্ষেত ঘুরে কলেজ ফটকে এসে শেষ হয়। এদিকে সড়ক অবরোধ করে সমাবেশ করায় এ সময় ওই সড়কে যান চলাচল বন্ধ হয়ে পড়ে।','অনলাইন প্রতিবেদক','11','2018-02-16 17:36:28.351989','2018-02-16 17:43:51.612814');
